import React, { useState } from "react";
import styled from "styled-components";

function BetArea(props) {
    // The parent component is responsible for managing both
    // the "commited" and "draft" email states. It tells this component
    // which one to display by passing props.email.
    return (
      <label>
        Bet: <input style={{maxWidth: '500px', textAlign:'center', fontSize:'3rem'}}
        autoFocus value={props.email} onChange={props.handleChange} />
      </label>
    );
  }

const BetTable = ({winColor, giveBet, handleGivePrize, balance, setBalance}) => {

    const [giveBetLocal, setGiveBetLocal] = useState(true)
    const [email, setEmail] = useState(null)
    const Grid = styled.div`
display: grid;
grid-template-columns: repeat(3, 1fr);
grid-template-rows: repeat(2, 1fr);
grid-column-gap: 15px;
grid-row-gap: 1rem;
margin-top: 1.5rem;
padding: 1rem;
    `

    const givePrize = () => {
        
        if (winColor === 'red') 
        {  
            setGiveBetLocal(false)
            setBalance((prev) => prev + 500)
        }
        else{ 
            setGiveBetLocal(false) 
            setBalance((prev) => prev - 500)
        }
        
    }
    if (!giveBet && !giveBetLocal)
    setGiveBetLocal(true)
    if (giveBet && giveBetLocal)
    {
        console.log(giveBet, giveBetLocal)
        givePrize()
        handleGivePrize()
    }
    const handleEmailChange = event => {
        setEmail(event.target.value);
      };
    
    return (
        <Grid>
            <div>
                <h1 style={{backgroundColor: 'red', color: 'white', borderRadius: '25%', textAlign: 'center'}}>Red</h1>
            </div>
            <div>
                <h1 style={{backgroundColor: 'green', color: 'white', borderRadius: '25%', textAlign: 'center'}}>Green</h1>
            </div>
            <div>
                <h1 style={{backgroundColor: 'black', color: 'white', borderRadius: '25%', textAlign: 'center'}}>
                    Black
                </h1>
            </div>
            <div style={{gridColumn: 'span 3', }}>
                <input  type="text"   />
                <ControlledEmailInput
          email={email}
          handleChange={handleEmailChange}
        />
        {console.log(email)}
            </div>
        </Grid>
    )
}

export default BetTable;
