import React from "react";
import styled from 'styled-components';
import Number from "./number";
import Line from "./line";
const Wheel = () => {

    let numbers = [
        {
            color: 'green',
            number: 0
        }
    ]

    for (let i = 1; i <15; i++)
    {
        let number = {
            color: i % 2 === 0 ? 'black': 'red',
            number: i
        }
        numbers.push(number);
    }
    //console.log(numbers)
    let Container = styled.div `
    max-width: 1200px;
    height: 100px;
    border: solid black 1px;
    //display: flex;
    //align-items: center;
    //justify-content: center;
    overflow: hidden;
    white-space: nowrap;
    `
    return (
        <Container>
            <Line />
            <div>
            {
                numbers.map((cell, i) => 
                <Number key={i}
                color={cell.color} number={cell.number} />)
            }
            </div>
        </Container>
    )
}

export default Wheel;
