import React from "react";
import styled from 'styled-components';
import Number from "./number";
import Line from "./line";
const Wheel = () => {

    let numbers = []

    for (let i = 1; i <15; i++)
    {
        let number = {
            color: i % 2 == 0 ? 'black': 'red',
            number: i
        }
        numbers.push(i);
    }
    
    let Container = styled.div `
    max-width: 1200px;
    height: 100px;
    border: solid black 1px;
    //display: flex;
    //align-items: center;
    //justify-content: center;
    overflow: hidden;
    white-space: nowrap;
    `
    return (
        <Container>
            <Line />
            <div>
            <Number color='green' number='0'/>
            <Number color='black' number='1'/>
            <Number color='red' number='2'/>
            <Number color='black' number='3'/>
            <Number color='red' number='4'/>
            <Number color='black' number='5'/>
            <Number color='red' number='6'/>
            <Number color='black' number='7'/>
            <Number color='red' number='8'/>
            <Number color='black' number='9'/>
            <Number color='red' number='10'/>
            <Number color='black' number='11'/>
            <Number color='red' number='12'/>
            <Number color='black' number='13'/>
            <Number color='red' number='14'/>
            <Number color='black' number='15'/>
            <Number color='red' number='16'/>
            </div>
        </Container>
    )
}

export default Wheel;
