import React, { useState } from  'react';
import RoulettePro from 'react-roulette-pro';
import 'react-roulette-pro/dist/index.css';

function getRandomInt(max) {
    return Math.floor(Math.random() * max);
  }

const prizes = [
  {
    image: 'https://i.ibb.co/sFB4rNy/resized-Image.png',
  },
  {
    image: 'https://i.ibb.co/sFB4rNy/resized-Image.png',
  },
  {
    image: 'https://i.ibb.co/sFB4rNy/resized-Image.png',
  },
  {
    image: 'https://i.ibb.co/sFB4rNy/resized-Image.png',
  },
  {
    image: 'https://i.ibb.co/sFB4rNy/resized-Image.png',
  },
];

const winPrizeIndex = 0;

const reproductionArray = (array = [], length = 0) => [
  ...Array(length)
    .fill('_')
    .map(() => array[Math.floor(Math.random() * array.length)]),
];

const reproducedPrizeList = [
  ...prizes,
  ...reproductionArray(prizes, prizes.length * 3),
 
];

const generateId = () =>
  Date.now().toString(36) + Math.random().toString(36).substring(2);

const prizeList = reproducedPrizeList.map((prize) => ({
  ...prize,
  id: generateId(),
}));

const Roulette = () => {
  const [start, setStart] = useState(false);
  const [counter, setCounter] = useState(10);
  const prizeIndex = getRandomInt(prizeList.length);

  React.useEffect(() => {
    const timer =
      counter > 0 && setInterval(() => setCounter(counter - 1), 1000);
      setStart(true)
    return () => clearInterval(timer);
  }, [counter]);

  const handlePrizeDefined = () => {
    console.log(`You have won ${prizeList[prizeIndex]['id']} ${prizeList.length}`);
    
    //setStart(false);
  };

  return (
    <>
     Till next roll: {counter}
      {counter == 0 ? setCounter(15): null}
      <RoulettePro
        prizes={prizeList}
        prizeIndex={prizeIndex}
        start={start}
        onPrizeDefined={handlePrizeDefined}
        spinningTime={10}
      />
     
    </>
  );
};

export default Roulette;
